import React, { useState } from 'react'

const App = () => {
  const anecdotes = [
      'If it hurts, do it more often.',
      'Adding manpower to a late software project makes it later!',
      'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.', 
      'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
      'Premature optimization is the root of all evil.',
      'Debugging is twice as hard as writing the code in the first place. Therefore, if you write code as cleverly as possible, you are, by definition, not smart enough to debug it.',
      'Programming without an extremely heavy use of console.log is same as if doctor would refuse to use x-rays or blood tests when diagnosing patients.',
  ]

  const [votes, setVotes] = useState(new Array(anecdotes.length + 1).join('0').split('').map(parseFloat))

  const [selected, setSelected] = useState(0)

  const newAnecdote = () => {
    console.log('new anecdote!')
    setSelected(getRandomInt(anecdotes.length))
  }

  const newVote = () => {
    console.log('vote button clicked')
    const copy = [...votes]
    copy[selected] += 1

    setVotes(copy)
  }

  return (
    <div>
      <h1>Anecdote of the day</h1>
      <p>{anecdotes[selected]}</p>
      <p>has {votes[selected]} votes</p>
      <Button handleClick={newVote} text='vote' />
      <Button handleClick={newAnecdote} text='next anecdote' />
      <h1>Anecdote with most votes</h1>
      <p>{anecdotes[getmostVoted(votes)]}</p>
    </div>
  )
}

function getRandomInt(max) {
  return Math.floor(Math.random() * max);
}

const Button = (props) => (
    <button onClick={props.handleClick}>
      {props.text}
    </button>
)

function getmostVoted(list) {
  let highest = 0
  let indexOfHighest = 0

  list.forEach(value => {
    if(value > highest) {
      highest = value
      indexOfHighest = list.indexOf(value)
    }
  })

  return indexOfHighest;
}

export default App
